"use strct";
$(function () {
  $('.js-btn').on('click', function () {        // js-btnクラスをクリックすると、
    $('.menu , .btn-line').toggleClass('open'); // メニューとバーガーの線にopenクラスをつけ外しする
  });
});

$(function() {
  $('.menu a').click(function() {
      $('.menu').removeClass('open');
     $('.btn-line').removeClass('open');
  });
});

